package main

import (
	"fmt"

	"github.com/gox/frm/log"
	"github.com/gox/frm/nw"
	"github.com/gox/frm/utils"
)

func main() {
	c, err := nw.NewTcpClient(":8080", 0, 0x12345678)
	if err != nil {
		log.Fatal(err)
	}

	defer c.Close()

	for i := 0; i < 10000; i++ {
		str := fmt.Sprintf(": %v", i)

		_, err = c.Write(*utils.Str2Bytes(str))
		if err != nil {
			log.Error(err)
			break
		}

		log.Info("sendSeq: %v", c.GetSendSeq())
		data, err := c.Read()
		if err != nil {
			log.Error(err)
			break
		}

		log.Info("recvSeq: %v, %v", c.GetRecvSeq(), *utils.Bytes2Str(data))
	}
}
