package main

import (
	"fmt"
	"sync"
	"sync/atomic"
	"time"

	"github.com/gox/frm/log"
	"github.com/gox/frm/nw"
)

const (
	N     = 100
	NCONN = 100
	HOST  = "18.166.30.234:9091"
	// HOST = "192.168.0.101:9091"
)

var (
	ssize = int64(0)
	rsize = int64(0)
	ntime = int64(0)
)

func testClient(wg *sync.WaitGroup) {
	defer wg.Done()

	c, err := nw.NewWsClient(HOST, 0)
	if err != nil {
		log.Fatal(err)
	}

	defer c.Close()

	for i := 0; i < N; i++ {
		str := fmt.Sprintf(": %v", i)
		wdata := []byte(str)

		wlen, err := c.Write(wdata)
		if err != nil {
			log.Error(err)
			break
		}

		atomic.AddInt64(&ssize, int64(wlen))

		rdata, err := c.Read()
		if err != nil {
			log.Error(err)
			break
		}

		if str != string(rdata) {
			log.Fatal("%s", string(rdata))
		}

		atomic.AddInt64(&rsize, int64(len(rdata)))
		atomic.AddInt64(&ntime, 1)
	}
}

func main() {
	tnow := time.Now()

	wg := sync.WaitGroup{}

	wg.Add(NCONN)
	for i := 0; i < NCONN; i++ {
		go testClient(&wg)
	}

	wg.Wait()

	spend := time.Since(tnow).Seconds()

	log.Info("请求总数: %d, 发送数据: %d Bytes, 接收数据: %d Bytes, 耗时: %v", ntime, ssize, rsize, spend)
	log.Info("QPS: %.2f, 吞吐量: %.2f Bytes/s", float64(ntime)/spend, float64(ssize+rsize)/spend)
}
