package main

import (
	"fmt"
	"time"

	"github.com/gox/frm/log"
	"github.com/gox/frm/nw"
	"github.com/gox/frm/utils"
)

const HOST = "127.0.0.1:9091"

func main() {
	c, err := nw.NewWsClient(HOST, time.Minute, nil, nil)
	if err != nil {
		log.Error(err)
		return
	}

	defer c.Close()

	for i := 0; i < 10000; i++ {
		str := fmt.Sprintf(": %v", i)

		_, err = c.Write(*utils.Str2Bytes(str))
		if err != nil {
			log.Error(err)
			break
		}

		log.Info("sendSeq: %v", c.GetSendSeq())
		data, err := c.Read()
		if err != nil {
			log.Error(err)
			break
		}

		log.Info("recvSeq: %v, %v", c.GetRecvSeq(), *utils.Bytes2Str(data))
	}
}
